func numberOfSubstrings(s string) int {
	// 记录0和1的分界点的位置
	divide := make([]int, 1)
	last := s[0]
	divideStartOneFlag := (last == 0x31)
	totalOne := 0
	for i := 1; i < len(s); i++ {
		if s[i] != last {
			if last == 0x31 {
				totalOne += (i - divide[len(divide)-1])
			}
			last = s[i]
			divide = append(divide, i)
		}
	}
	if last == 0x31 {
		totalOne += (len(s) - divide[len(divide)-1])
	}
	divide = append(divide, len(s))

	counter := 0
	divideStartIndex := 0

	for start := 0; start < len(s); start++ {
		cntOne := 0
		one := divideStartOneFlag
		i := divideStartIndex + 1
		if one {
			counter += divide[i] - start
			cntOne += divide[i] - start
			one = false
			i++
		}

		for ; i < len(divide); i++ {
			begin := divide[i-1]
			if begin < start {
				begin = start
			}
			end := divide[i]
			if one {
				cntOne += end - begin
				one = false
				continue
			}

			cntZero := begin - start - cntOne
			for j := begin; j < end; j++ {
				p := j + 1
				if p >= end {
					if i+1 < len(divide) {
						p = divide[i+1]
					} else {
						p = len(s)
					}
				}
				cntZero++
				if totalOne < cntZero*cntZero {
					break
				} else {
					counter += max(p-j-max(cntZero*cntZero-cntOne, 0), 0)
				}
			}
			one = !one
		}
		if start == divide[divideStartIndex+1]-1 {
			divideStartOneFlag = !divideStartOneFlag
			divideStartIndex++
		}
	}
	return counter
}
